import { Button } from '@vkontakte/vkui';
import React, { useState } from 'react'

const MessageBoxButton = (props) => {

    const sendMessage = async (e) => {
        e.preventDefault();
        const newMsg = {
            ...msg, id: Date.now()
        }
        create(newMsg)
        setMsg({ sender_name: '', text: '' })
    }

    return (
        <Button {...props}
            type="submit"
            // onClick={sendMessage}
            size="l"
        >
            Send
        </Button>

    )
}

export default MessageBoxButton;
